import React, { useState, useEffect } from "react";
import config from "../config";
import { MapContainer } from 'react-leaflet/MapContainer'
import { Marker } from 'react-leaflet/Marker'
import { Popup } from 'react-leaflet/Popup'
import { TileLayer } from 'react-leaflet/TileLayer'
import 'leaflet/dist/leaflet.css'

const URL = `${config.WEBSOCKET_URL}`;
let connect_message = '{"type": "join", "id": "user id", "username": "username"}';
const position = [51.505, -0.09];

const Flights = () => {
    const [ws, setWs] = useState(new WebSocket(URL));
    const [message, setMessage] = useState([]);
    const [messages, setMessages] = useState([]);
    const [flights, setFlights] = useState([]);

    useEffect(() => {
	    ws.onopen = () => {
	      console.log('WebSocket Connected');
        ws.send(connect_message);
	    }

        ws.onmessage = function (event) {
            const message = JSON.parse(event.data);
            try {
                if (message.hasOwnProperty("flights")) {
                    var result = [];
                    for(var i in message["flights"])
                        result.push([i]);
                    setFlights([result]);
                    // console.log(message["flights"]);
                }
            } catch (err) {
                // whatever you wish to do with the err
            }

            };

	    return () => {
	      ws.onclose = () => {
	        console.log('WebSocket Disconnected');
	        setWs(new WebSocket(URL));
	      }
	    }
  	}, [ws.onmessage, ws.onopen, ws.onclose]);
    return (
        <div className="map">
            <MapContainer center={[51.505, -0.09]} zoom={8} scrollWheelZoom={false}>
                <TileLayer
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />
                <Marker position={[51.505, -0.09]}>
                    <Popup>
                    A pretty CSS3 popup. <br /> Easily customizable.
                    </Popup>
                </Marker>
            </MapContainer>
        </div>
    )
}

export default Flights;
