    //     .finally(() => setLoading(false));
    // }, []);
    // console.log(flights);
    useEffect(() => {
        const ws = new WebSocket("wss://tarea-1.2022-2.tallerdeintegracion.cl/connect");

        const apiCall = {
            type: "join",
            id: "user id",
            username: "username"
        };

        ws.onopen = (event) => {
            ws.send(JSON.stringify(apiCall));
            console.log("abierto");
        };

        ws.onmessage = function (event) {
            const json = JSON.parse(event.data);
            try {
            if ((json.event = "data")) {
                setFlights(json.data.flights.slice(0, 5));
            }
            } catch (err) {
            console.log(err);
            }
        };

    }, []);

    return (
        <div>
            <div>oli</div>
            <div>
                {flights.map((vuelo) => (
                <p>{`${vuelo}`}</p>   
                )
                )}
            </div>
        </div>
    );

};

export default Flights;
