                //     console.log(message["take-off"]);
                // }

                // if (message.hasOwnProperty("landing")) {
                //     console.log(message["landing"]);
                // }

                // if (message.hasOwnProperty("crashed")) {
                //     console.log(message["crashed"]);
                // }

                // if (message.hasOwnProperty("message")) {
                //     console.log(message["message"]);
                // }

            } catch (err) {
                // whatever you wish to do with the err
            }

            };

	    return () => {
	      ws.onclose = () => {
	        console.log('WebSocket Disconnected');
	        setWs(new WebSocket(URL));
	      }
	    }
  	}, [ws.onmessage, ws.onopen, ws.onclose]);
    return (
        <div className="map">
            <MapContainer center={[51.505, -0.09]} zoom={1} scrollWheelZoom={false}>
                <TileLayer
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />
                {/* {flights2.length > 0 && 
                  // hacerle un filtro a flights 2 pa q solo pesque elementos de flight definidos
                  flights2.map( (element, index) => {                  
                    <Marker position={[flights[element].departure.location["lat"], flights[element].departure.location["long"]]} key={index} icon={markerIcon} >
                      <Popup>
                        <p>aeropuesto</p>

                        <p>Long:</p>
                        <p>Tag: </p>
                      </Popup>
                    </Marker>

                  })
                } */}
